import React, { Component } from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import friends from "./friends.json";

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends,
    topscore: 0,
    score: 0
  };



  removeFriend = id => {


   friends.map(friend => {


      if (friend.id === id && friend.clicked === 0) {
        friend.clicked=1;
        this.setState({ friends: friends, score: this.state.score + 1, topscore: this.state.topscore + 1 });
        return true;  
      } else if (friend.id === id && friend.clicked === 1) {

       this.setState({   score: 0 });
       return false;
      }


      return false;
    });

     friends.sort(() => Math.random() - 0.5);
  }



  render() {
    return (
      <Wrapper>
        <Title>Friends List score: {this.state.score} {this.state.topscore}  </Title>
        {this.state.friends.map(friend => (
          <FriendCard
            removeFriend={this.removeFriend}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            occupation={friend.occupation}
            location={friend.location}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
